{
    "世界": {
        "$meta": {
            "extensible": false // 世界的结构是固定的，不允许AI添加或删除“日期”、“天气”等核心字段。
        },
        "日期": ["2025-08-15", "当前世界日期，格式: YYYY-MM-DD"],
        "星期": ["星期五", "当前世界是星期几"],
        "季节": ["夏", "当前世界所处季节"],
        "时间": ["14:30", "当前世界时间，24小时制"],
        "天气": ["晴", "['晴', '多云', '雨', '雪', '雷暴']"]
    },
    "玩家": {
        "$meta": {
            "extensible": false // 玩家的核心属性是固定的。
        },
        "姓名": ["{{USER}}", "用户姓名"],
        "金钱": [5000, "货币单位: 円"],
        "点数": [0, "用于啪啪商城"],
        "体质": [80, "当前体质值 / 上限100"],
        "体力": [100, "当前体力值 / 上限100"],
        "当前世界": ["主世界", "用户当前所处世界"],
        "临时力量": [
            ["无", "$__META_EXTENSIBLE__$"], // 数组内有这个标记，意味着AI可以向其中 assign 或 remove 元素。
            "用户在处理异界订单时赋予的临时力量"
        ],
        "特殊奖励": [
            ["无", "$__META_EXTENSIBLE__$"], // 同上，允许增删。
            "用户完成推荐订单和异界订单后获得的特殊奖励。"
        ]
    },
    "当前情景": {
        "$meta": {
            "extensible": false // 场景的核心要素是固定的。
        },
        "已接受订单的异世界": {
            "$meta": { "extensible": true } // 这个对象允许在后续开发中扩展。
        },
        "订单目标": ["无", "当前任务的简要描述"],
        "当前客户名": ["无", "指向客户列表中的一个键"],
        "当前客户序号": [0, "当前交互对象"],
        "当前位置": ["主角公寓", "当前场景发生的地点"],
        "待办事项": [
            ["$__META_EXTENSIBLE__$"], // 允许AI向待办事项列表中添加内容。
            "用户待办事项，最多存储5项。"
        ]
    },
    "客户列表": {
        "$meta": {
            "extensible": true // 核心！允许AI使用 _.assign('客户列表', '新客户名', {...}) 来添加新的客户。
        }
    }
}
